                'BARRILITO',
                'CRAYOLA',
                'FANTASY',
                'IDEAL',
                'MAPED',
                'NORMA',
                'PELIKAN',
                'POLITEC',
                'PRISMACOLOR',
                'RESISTOL',
                'SCRIBE',
                'STABILO',
                'ZEBRA'));
                
